import { Action } from "redux";
import * as actions from "./actions";

// general for redux
type inferValueType<T> = T extends { [key: string]: infer U } ? U : never;
const iL = <U, T extends U>(arg: T): T => arg;
export const inferLiteral = <T extends string>(arg: T): T => iL(arg);
export type <%-options.entityActionTypes-%> = ReturnType<inferValueType<typeof actions>>;
<% Object.keys(worker).forEach(elem => { %>
// <%- worker[elem].comment %>
<% for (let item of worker[elem].element){ -%>
export type <%-item.actionPayload-%> = {};
<% } -%>
<% for (let item of worker[elem].element){ -%>
<% if(item.actionType) { %>
export type <%-item.actionType-%> = (payload: <%-item.actionPayload-%>) => <%-options.entityActionTypes-%>;
export interface <%-item.sagaType-%> extends Action {
  payload: <%-item.actionPayload-%>;
}
<% } -%>
<% } -%>
<% }); -%>
// main type
export type <%-options.mainType-%> = {};

